    hDeltaDCAzVsTrkPt  -> Fill(pt, deltaDcaZ);
    hDeltaEtaVsTrkPt   -> Fill(pt, deltaEta);
    hDeltaPhiVsTrkPt   -> Fill(pt, deltaPhi);
    hDeltaPtVsTrkPt    -> Fill(pt, deltaPt);

