    if (!isInMvtxCut) continue;

    // fill histograms
    FillTrackHistograms(6, recoTrkVars, trueTrkVars, recoPhysVars, truePhysVars);


    // fill with-pileup track histograms
    hTrackNMms_PU         -> Fill(pu_nlmms);
