  oddOnly -> SetFillColor(fColFilL);
  oddOnly -> SetFillStyle(fFilL);
  oddOnly -> SetLineColor(fColLinL);
  oddOnly -> SetLineStyle(fLinL);
  oddOnly -> SetTextFont(FTxt);
  oddOnly -> SetTextAlign(fAln);
  oddOnly -> SetTextColor(fColTxtL);
  oddOnly -> AddEntry(hWeirdPt,  sLeg[2].Data(), "pf");
